import React from 'react';
import { motion } from 'framer-motion';
import Achievements from './Achievements';
import achievementIcon from './assets'; // Update the path as necessary

const AchievementsSection = () => {
  return (
    <motion.div
      className="cards1"
      style={{
        padding: '20px',
        backgroundColor: 'rgba(255, 255, 255, 0.8)',
        marginTop: '40px',
        borderRadius: '8px',
        boxShadow: '0 4px 10px rgba(0, 0, 0, 0.3)',
        display: 'flex', // Use flexbox for layout
        alignItems: 'center', // Center align items vertically
      }}
      whileHover={{ scale: 1.05 }} // Scale up on hover
      transition={{ duration: 0.3 }} // Duration for the scaling transition
    >
      <img
        src={achievementIcon}
        alt="Achievements Icon"
        style={{
          width: '40px', // Set a width for the icon
          height: '40px', // Set a height for the icon
          marginRight: '10px', // Space between the icon and title
        }}
      />
      <motion.h2
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        style={{ textAlign: 'center', marginBottom: '15px', flex: 1 }} // flex: 1 allows title to take available space
      >
        Achievements
      </motion.h2>
      <Achievements />
    </motion.div>
  );
};

export default AchievementsSection;
