import React, { useState, useEffect } from 'react';
import AchievementsSection from './AchievementsSection';
import UpcomingEvents from './UpcomingEvents';
import { motion } from 'framer-motion';
import './Home.css';

const Home = () => {
  const [isBlurred, setIsBlurred] = useState(false); // For blurring video effect
  const [showTitle, setShowTitle] = useState(false); // For showing the title

  useEffect(() => {
    // Delay to blur the video and show the title after 6 seconds
    const timer = setTimeout(() => {
      setIsBlurred(true);
      setShowTitle(true);
    }, 6000);

    return () => clearTimeout(timer);
  }, []);

  const handleScrollToAchievements = () => {
    document.getElementById('achievements-section').scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div>
      {/* Background Video Section */}
      <div style={{ position: 'relative', width: '100%', height: '80vh', overflow: 'hidden' }}>
        <video
          autoPlay
          loop
          muted
          className={isBlurred ? 'blurred' : ''}
          style={{ position: 'absolute', width: '100%', height: '100%', objectFit: 'cover', zIndex: -1 }}
        >
          <source src="/video.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>

        {/* Welcome Title */}
        <motion.h1
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: showTitle ? 1 : 0, y: 0 }}
          transition={{ duration: 1 }}
          style={{
            color: '#FFF8E8',
            textShadow: '2px 2px 4px rgba(0, 0, 0, 0.7)',
            fontSize: '4rem',
            textAlign: 'center',
            marginTop: '30vh',
            zIndex: 1,
          }}
        >
          Welcome to Computer Department
        </motion.h1>

        {/* Scroll to Achievements Button */}
        <motion.button
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: showTitle ? 1 : 0, y: 0 }}
          transition={{ duration: 0.5 }}
          onClick={handleScrollToAchievements}
          style={{
            position: 'absolute',
            bottom: '10vh',
            left: '50%',
            transform: 'translateX(-50%)',
            padding: '10px 20px',
            backgroundColor: '#AAB396', /* Matching your palette */
            color: '#FFF',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            fontSize: '1.2rem',
            boxShadow: '0 4px 6px rgba(0, 0, 0, 0.2)',
          }}
        >
          Go to Achievements
        </motion.button>
      </div>

      {/* Achievements Section with ID for scrolling */}
      <div id="achievements-section">
        <AchievementsSection />
      </div>

      {/* Upcoming Events Section */}
      <div
        id="events-section"
        style={{
          padding: '20px',
          backgroundColor: 'rgba(255, 255, 255, 0.8)',
          marginTop: '20px',
          borderRadius: '8px',
          boxShadow: '0 4px 10px rgba(0, 0, 0, 0.3)',
        }}
      >
        <motion.h2
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          style={{ textAlign: 'center', marginBottom: '15px' }}
        >
          Upcoming Events
        </motion.h2>
        <UpcomingEvents />
      </div>
    </div>
  );
};

export default Home;
