import React, { useState, useEffect } from 'react';
import AchievementsSection from './AchievementsSection';
import UpcomingEvents from './UpcomingEvents';
import { motion } from 'framer-motion';
import './Home.css';

const Home = () => {
  const [isBlurred, setIsBlurred] = useState(false);
  const [showTitle, setShowTitle] = useState(false);

  useEffect(() => {
    // Delay to blur the video and show the title after 6 seconds
    const timer = setTimeout(() => {
      setIsBlurred(true);
      setShowTitle(true);
    }, 6000);

    return () => clearTimeout(timer);
  }, []);

  useEffect(() => {
    const handleScroll = () => {
      const achievementsSection = document.getElementById('achievements-section');
      if (window.scrollY > 200) {  // You can adjust this value
        achievementsSection.scrollIntoView({ behavior: 'smooth' });
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll); // Clean up the event listener
    };
  }, []);

  return (
    <div>
      {/* Background Video Section */}
      <div style={{ position: 'relative', width: '100%', height: '80vh', overflow: 'hidden' }}>
        <video
          autoPlay
          loop
          muted
          className={isBlurred ? 'blurred' : ''}
          style={{ position: 'absolute', width: '100%', height: '100%', objectFit: 'cover', zIndex: -1 }}
        >
          <source src="/video.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>

        {/* Welcome Title */}
        <motion.h1
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: showTitle ? 1 : 0, y: 0 }}
          transition={{ duration: 1 }}
          style={{
            color: '#FFF8E8',
            textShadow: '2px 2px 4px rgba(0, 0, 0, 0.7)',
            fontSize: '4rem',
            textAlign: 'center',
            marginTop: '30vh',
            zIndex: 1,
          }}
        >
          Welcome to Computer Department
        </motion.h1>
      </div>

      {/* Achievements Section */}
      <div id="achievements-section">
        <AchievementsSection />
      </div>

      {/* Upcoming Events Section */}
      <div
        style={{
          padding: '20px',
          backgroundColor: 'rgba(255, 255, 255, 0.8)',
          marginTop: '20px',
          borderRadius: '8px',
          boxShadow: '0 4px 10px rgba(0, 0, 0, 0.3)',
        }}
      >
        <motion.h2
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          style={{ textAlign: 'center', marginBottom: '15px' }}
        >
          Upcoming Events
        </motion.h2>
        <UpcomingEvents />
      </div>
    </div>
  );
};

export default Home;
